class App extends Component {
  constructor(){
    super();

    localStorage.clear()

    this.state = {
      editorText: "",
      currentNoteID: 0
    }
  }

  render(){
    return(
      <div class="App">
        <div class="row">
          <h1 class="column"> notes app </h1>
        </div>
        <div class="row">
          <Editor
          />
        </div>
      </div>
    )
  }
}

export default App;
