import React, { Component } from 'react';
import NotePreview from './NotePreview.js';

const liStyle = {
  listStyleType: 'none'
};

const buttonStyle = {
  paddingLeft: '10px',
  marginLeft: '5px',
  width: '100%',
  marginRight: '5px'
}

class NoteList extends Component {
  constructor(props){
    super(props);

    this.handleClick = this.handleClick.bind(this);
    this.createNoteList = this.createNoteList.bind(this);
  }

  handleClick(noteID){
    this.props.handleNoteClick(noteID);
  }

  createNoteList(){
    let notes = [];
    let i = 0;
    for (i = 0; i < localStorage.length; i++){
      const id = i;
      if(localStorage[i] === ""){
        notes.push(<li style={liStyle} key={i.toString()}><div className="row">New Note</div></li>)
      } else {
        notes.push(<li style={liStyle} key={i.toString()}><button style={buttonStyle}className=" row" onClick={()=>{this.handleClick(id)}}>
          {localStorage[i]}
        </button></li>)
      }
    }
    return notes;
  }

