import React, { Component } from 'react';
import SimpleMDE from "react-simplemde-editor"

class Editor extends Component {
  constructor(){
    super();
    this.state = {
      editorText: "",
      currentNoteID: 0
    }
    this.handleSaveClick = this.handleSaveClick.bind(this);
  }

  handleSaveClick(){
    alert(this.state.editorText);
    localStorage.setItem(this.state.currentNoteID, this.state.editorText);
  }

  handleChange = (value) => {
    this.setState({
      editorText: value
    })
  }

  render(){
    return(
      <div class="Editor column column-s75">
        <SimpleMDE
          options={{
            autofocus: true
          }}
          onChange={this.handleChange}
        />
        <button class="button button-clear" onClick={this.handleSaveClick}> save </button>
      </div>
    )
  }
}

export default Editor;
