import React, { Component } from 'react';
import Sidebar from './components/Sidebar.js'
import Editor from './components/Editor.js'
import 'milligram'


class App extends Component {
  constructor(){
    super();
    this.state = {
      editorText: "",
      currentNoteID: 0
    }

    //initialize localStorage for first Note
    localStorage.setItem(this.state.currentNoteID, this.state.editorText);

    this.handleSaveClick=this.handleSaveClick.bind(this);
    this.handleChange=this.handleChange.bind(this);
    this.addNewNote=this.addNewNote.bind(this);
  }

  // handleSaveClick(event){
  //   console.log("Savings Note ID: " + this.state.currentNoteID);
  //   event.preventDefault();
  //   localStorage.setItem(this.state.currentNoteID, this.state.editorText);
  // }

  handleChange(value){
    this.setState({
      editorText: value
    })
    localStorage.setItem(this.state.currentNoteID, this.state.editorText);
  }

  addNewNote(){
    this.setState({
      currentNoteID: this.state.currentNoteID+1,
      editorText: ""
    })
    console.log("Add New Note: Note ID: " + this.state.currentNoteID);
    localStorage.setItem(this.state.currentNoteID, this.state.editorText);
  }

  render(){
    return(
      <div class="App">
        <div class="row">
          <h1 class="column"> notes app </h1>
        </div>
        <div class="row">
          <Sidebar
            onNewNoteClick = {this.addNewNote}
          />
          <Editor
            handleChange = {this.handleChange}
            handleSaveClick = {this.handleSaveClick}
            text={this.state.editorText}
          />
        </div>
      </div>
    )
  }
}

export default App;
