import React, { Component } from 'react';
import NotePreview from './NotePreview.js';

class NoteList extends Component {
  constructor(props){
    super(props);

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(noteID){
    alert("handling click from NoteList with ID: "+noteID);
    this.props.handleNoteClick(noteID);
  }

  render(){
    let notes = [];
    let i = 0;
    for (i = 0; i < localStorage.length; i++){
      if(localStorage[i] === ""){
        notes.push(<div className="row">New Note</div>)
      } else {
        // notes.push(<button className=" row" onClick={()=>{this.handleClick(i)}}>
        //   {localStorage[i]}
        // </button>)
        notes.push(localStorage[i]);
      }
    }

    notes = notes.map((note)=>
      <button className="row" onClick={()=>{this.handleClick(i)}}>
        <li>note</li>
      </button>
    )

    return <ul>{notes}</ul>;
  }
}

export default NoteList;
