import React, { Component } from 'react';
import Sidebar from './components/Sidebar.js'
import Editor from './components/Editor.js'
import AddNoteButton from './components/AddNoteButton.js'
import 'milligram'

const NoteIDContext = React.createContext()

class App extends Component {
  constructor(){
    super();

    localStorage.clear()

    this.state = {
      currentNoteID: 0
    }

    localStorage.setItem(this.state.currentNoteID, "");
  }

  handleClick = () =>{
    this.setState({
      currentNoteID: this.state.currentNoteID + 1
    })
    localStorage.setItem(this.state.currentNoteID, " ")
  }

  handleChange = value => {
    localStorage.setItem(this.state.currentNoteID, value);
  }

  addNewNote = () =>{
    alert("Add New Note");
    let nextNoteID = localStorage.length;
    localStorage.setItem(nextNoteID, "");
    this.setState({currentNoteID: nextNoteID})
  }

  render(){
    return(
      <div class="App">
        <div class="row">
          <h1 class="column"> notes app </h1>
        </div>
        <div class="row">
          <div class="column column-25">
            <AddNoteButton handleClick = {this.handleClick}/>
          </div>
          <div class="column column-75">
            <Editor
            handleChange = {this.handleChange}
            currentNoteID = {this.state.currentNoteID}
            />
          </div>
        </div>
      </div>
    )
  }
}

export default App;
